				&zia.ZIAAdminUsersAdminScopeArgs{
					Type: pulumi.String("DEPARTMENT"),
					ScopeEntities: &zia.ZIAAdminUsersAdminScopeScopeEntitiesArgs{
						Ids: pulumi.IntArray{
							*pulumi.Int(engineering.Id),
						},
					},
				},
			},
		})
		if err != nil {
			return err
		}
		return nil
	})
}
