    departments: {
        ids: [engineering.then(engineering => engineering.id)],
    },
    groups: {
        ids: [normalInternet.then(normalInternet => normalInternet.id)],
    },
    timeWindows: {
        ids: [workHours.then(workHours => workHours.id)],
    },
});
