    email="john.ashcroft@acme.com",
    password="P@ssw0rd123*",
    groups=zia.ZIAUserManagementGroupsArgs(
        ids=normal_internet.id,
    ),
    department=zia.ZIAUserManagementDepartmentArgs(
        id=engineering.id,
    ))
